import React from "react";
import "../App.css";
import ProductComponent from "./ProductComponent";
class Products extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      productsArray: []
    };
  }
  componentDidMount() {
    this.fetchProducts();
  }
  renderProducts() {
    if (this.state.productsArray[0]) {
      const title = this.state.productsArray[0].title;
      console.log(title);
      const productsArray = this.state.productsArray;
      const productsRendered = productsArray.map(product => {
        return (
          <ProductComponent
            id={product.id}
            title={product.title}
            image={product.image}
            product_type={product.product_type}
            variants={product.variants}
          />
        );
      });

      return <div className="product-component-main-container">{productsRendered}</div>;
    }
    return;
  }

  fetchProducts = async () => {
    const response = await fetch("/api/v1/inventory", {
      method: "GET",
      headers: {
        "Content-Type": "application/json"
      }
    })
      .then(response => {
        return response.json();
      })
      .then(products => {
        this.setState({ productsArray: products.data });
      });
  };

  render() {
    return <div className="products-page-wrapper">{this.renderProducts()}</div>;
  }
}

export default Products;
