import React from "react";
import "../App.css";
import ProductComponent from "./ProductComponent";
class Products extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      productsArray: []
    };
  }
  componentDidMount() {
    this.fetchProducts();
  }
  renderProducts() {
    // switch (this.state.productsArray) {
    //   case undefined:
    //     return <div>Loading</div>;
    //   case null:
    //     return <div>StilL Loading</div>;
    //   default:
    //     return(
    //         <div>
    //             <div>{this.state.productsArray[0]}</div>
    //         </div>
    //     ) 
    // }
    if (this.state.productsArray[0]){
        const title = this.state.productsArray[0].title;
        console.log(title);
       return  <ProductComponent link = {title}/>
    }
    return;

  }


  fetchProducts = async () => {
    const response = await fetch("/api/v1/inventory", {
      method: "GET",
      headers: {
        "Content-Type": "application/json"
      }
    })
      .then(response => {
        return response.json();
      })
      .then(products => {
        this.setState({ productsArray: products.data });
      });
  };

  render() {
    return (
      <div className="products-page-wrapper">
          {this.renderProducts()}
      </div>
    );
  }
}

export default Products;
