import React from "react";
import "../App.css";
import ProductComponent from "./ProductComponent";
class Products extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      productsArray: []
    };
  }
  componentDidMount() {
    this.fetchProducts();
  }
  renderProducts() {
    switch (this.state.productsArray) {
      case undefined:
        return <div>Loading</div>;
      case null:
        return <div>StilL Loading</div>;
      default:
        return(
            <div>
                <ProductComponent/>
            </div>
        ) 
    }
  }

  fetchProducts = async () => {
    const response = await fetch("/api/v1/inventory", {
      method: "GET",
      headers: {
        "Content-Type": "application/json"
      }
    })
      .then(response => {
        return response.json();
      })
      .then(products => {
        this.setState({ productsArray: products.data });
        console.log(this.state.productsArray[0].title);
      });
  };

  render() {
    return (
      <div className="products-page-wrapper">
        <h1>{this.renderProducts()}</h1>
      </div>
    );
  }
}

export default Products;
