    constructor(props) {
        super(props);
        this.state = {
        productsArray:[]
        };
      }
    componentDidMount(){
        this.fetchProducts()
    }


    fetchProducts = async ()=>{
        const response = await fetch('/api/v1/inventory', {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json',
            }

          }).then((response)=>{
            return response.json();
          }).then(products=>{            
              this.setState({productsArray:products.data})
              console.log(this.state.productsArray)
          });
    }

render(){


    return (
