import React from 'react';
import "../App.css"
import ProductComponent  from './ProductComponent'
class Products extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
        productsArray:[]
        };
      }
    componentDidMount(){
        this.fetchProducts()
    }
    renderProducts = ()=>{
        return <ProductComponent link={this.state.productsArray[0].id}/>           

    }

    fetchProducts = async ()=>{
        const response = await fetch('/api/v1/inventory', {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json',
            }

          }).then((response)=>{
            return response.json();
          }).then(products=>{            
              this.setState({productsArray:products.data})
              console.log(this.state.productsArray[0].title)
              this.renderProducts()
          });
    }

render(){


    return (
        <div className="products-page-wrapper">
            {this.renderProducts()}
        </div>
    )
}
}

 export default Products;
