import React from "react";
import "../App.css";
class ProductComponenet extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
    response:null,
    id:this.props.product.id
    };
  }
    componentDidMount(){
      this.fetchRequestedId()
    }
    fetchRequestedId = async props => {
      const response = await fetch(
        `/api/v1/inventory/${this.state.id}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json"
          }
        }
      )
        .then(response => {
          return response.json();
        })
        .then(data => {
          this.setState({ response: data });
          // this.daysDiffFinder();
          // this.getInStock();
          // this.velocityFinder();
          console.log(this.state.velocityObject, "after then function");
        });
    };
    render(){

      var variants = this.props.variants;
    var totalInventory = 0;
    var numberOfVariants = variants.length;
    variants.map(item=>{
      totalInventory = item.inventory_quantity + totalInventory
  }) 
  return (
    <div className="product-container">
      <div className="product-image-container">
        <div
          className="demo-card-image mdl-card mdl-shadow--2dp"
          style={{
            background: ` url(${this.props.image}) center / cover`
          }}
        >
          <div className="mdl-card__title mdl-card--expand" />
          <div className="mdl-card__actions">
            <span className="demo-card-image__filename"></span>
          </div>
        </div>
      </div>
      <div className = "product-des-container">
        <h6>{this.props.variants[0].price}</h6>
        <h6>{totalInventory}{(numberOfVariants == 1) ? '' : ` of ${numberOfVariants}`  }</h6>
        <h6>{this.props.product_type}</h6>
        <h6>{this.props.title}</h6>
      </div>
          <a href={`/a/dashboard/products/inventory/${this.props.id}`}>
          <button class="mdl-button mdl-js-button mdl-button--raised mdl-button--colored" >
         Details
        </button>
          </a>


    </div>
  );
        }
};

export default ProductComponenet;
