import React from "react";
import Chart from "chart.js";
import { Line } from "react-chartjs-2";

class InventoryChart extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: this.props.data,
      datesArr:[]
    };
  }
  componentDidMount() {
    //console.log(this.props.data)
    this.handleData();
  }


  handleData = () => {
    const datasetsData = [];
    const dates = [];
    var datasetsObject = {};
    const data = this.state.data;
    var datesArray = data[0].inventory;
    datesArray[3].date = "2019-08-30T16:05:55.913Z";

    function SortDate() {
      data[0].inventory.sort(function(temp1, temp2) {
        return new Date(temp1.date) - new Date(temp2.date);
      });
    }
    SortDate();

    console.log(data[0].inventory);
    console.log(data[0]);

    data[0].inventory.map(inv=>{

    })
    data[0].variants.map((variant, i) => {
      datasetsObject[variant.id] = {
        "label": variant.title,
        "inventory": []
      }
    //  console.log(object)
    });
    console.log(datasetsObject)

    data[0].inventory.map((inv) => {
      dates.push(inv.date)
      inv.variants.map((variant) => {
        console.log(variant);
        datasetsObject[variant.id].inventory.push(variant.inventory_quantity);
      });
    });
    console.log(datasetsObject);
    console.log(dates)
    dates.map(date=> {
      date.split("T")[0]
    })
    this.setState({datesArr:dates})

  };
  render() {
    var data = {
      labels: this.state.datesArr,
      datasets: [
        {
          label: "My First dataset",
          //backgroundColor: "rgb(255, 99, 132)",
          borderColor: "rgb(255, 99, 132)",
          data: [0, 10, 5, 2, 20, 30, 45]
        }
      ]
    };
    console.log(this.state.datesArr)
    return (
      <div className="chart-comp-container">
        <Line data={data} />
      </div>
    );
  }
}
export default InventoryChart;
