import React from "react";
import InventoryChart from "./InventoryChart";
import Loading from "./Loading";
import moment from 'moment'
class Inventory extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      requestedId: this.props.match.params.id,
      response: null
    };
  }
  componentDidMount() {
    this.fetchRequestedId();
  }

  fetchRequestedId = async props => {
    const response = await fetch(
      `/api/v1/inventory/${this.state.requestedId}`,
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json"
        }
      }
    )
      .then(response => {
        return response.json();
      })
      .then(data => {
        this.setState({ response: data });
      });
  };
  getInStock () {
    const mostUpdatedInv = this.state.response[0].inventory[this.state.response[0].inventory.length - 1]
    var inStock = mostUpdatedInv.variants.map(item=> {
      return <h6>Size: {item.title} In Stock: {item.inventory_quantity}</h6>
    })
    return inStock
  }

  velocityFinder = () => {
    const oldestDate = this.state.response[0].inventory[0].date;
    const newestDate = this.state.response[0].inventory[this.state.response[0].inventory.length - 1].date
    console.log(oldestDate, newestDate)
  }

  renderContent() {



    if (this.state.response) {
      /************html*************/
      const imageURL = this.state.response[0].image





      return (
        <div>
          <div className="product_info_container">
            <div className="product_info_card">
              <div className="demo-card-wide mdl-card mdl-shadow--2dp">
                <div className="mdl-card__title" style={{backgroundImage:`url(${imageURL})`}}>
                  <h2 className="mdl-card__title-text"  >{this.state.response[0].title} - {this.state.response[0].product_type}</h2>
                </div>
                <div className="mdl-card__supporting-text">
                {this.getInStock()}
                </div>
                <div className="mdl-card__actions mdl-card--border">
                  <a className="mdl-button mdl-button--colored mdl-js-button mdl-js-ripple-effect">
                    Get Started
                  </a>
                </div>
                <div className="mdl-card__menu">
                  <button className="mdl-button mdl-button--icon mdl-js-button mdl-js-ripple-effect">
                    <i className="material-icons">share</i>
                  </button>
                </div>
              </div>
            </div>
            <div className="stat_container">
              <div className="demo-card-wide mdl-card mdl-shadow--2dp">
                <div className="mdl-card__title">
                  <h2 className="mdl-card__title-text">Welcome</h2>
                </div>
                <div className="mdl-card__supporting-text">
                  Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                  Mauris sagittis pellentesque lacus eleifend lacinia...
                </div>
                <div className="mdl-card__actions mdl-card--border">
                  <a className="mdl-button mdl-button--colored mdl-js-button mdl-js-ripple-effect">
                    Get Started
                  </a>
                </div>
                <div className="mdl-card__menu">
                  <button className="mdl-button mdl-button--icon mdl-js-button mdl-js-ripple-effect">
                    <i className="material-icons">share</i>
                  </button>
                </div>
              </div>
            </div>
          </div>
          <InventoryChart data={this.state.response} />
        </div>
      );
    }
    return <Loading />;
  }

  render() {
    return <div className="page_container">{this.renderContent()}</div>;
  }
}
export default Inventory;
